
var koa = require('./');

var app = koa();

app.use(function *one(next){
  console.log('>> one');
  yield next;
  console.log('<< one');
})

app.use(function *two(next){
  console.log('>> two');
  yield next;
  console.log('<< two');
})

app.wrap(function *(next){
  console.log('>> a');
  yield next;
  console.log('<< a');
})

app.wrap(function *(next){
  console.log('>> b');
  yield next;
  console.log('<< b');
})

app.wrap(function *(next){
  console.log('>> c');
  yield next;
  console.log('<< c');
})

app.listen(3000)